class Animal {
  void eat() {
    System.out.println("This animal eats food.");
  }
}

class Dog extends Animal {
  @Override
  void eat() {
    System.out.println("Dog eats kibble.");
  }
}

public class TestInheritance {
  public static void main(String[] args) {
    Animal myAnimal = new Animal();
    myAnimal.eat();
    Dog myDog = new Dog();
    myDog.eat();
  }
}
